INSERT INTO `animeaux` (`id`, `nom`, `type`, `espece`, `description`, `image_path`, `created_at`, `updated_at`) VALUES
(1, 'Lion', 'mammifere', 'Panthera leo', 'Le lion est un grand félin connu comme le \"roi des animaux\". Les mâles sont reconnaissables à leur imposante crinière.', 'images/animaux/lion.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(2, 'Éléphant d\'Afrique', 'mammifere', 'Loxodonta africana', 'Plus grand mammifère terrestre, l\'éléphant d\'Afrique est reconnaissable à ses grandes oreilles et ses défenses en ivoire.', 'images/animaux/elephant.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(3, 'Girafe', 'mammifere', 'Giraffa camelopardalis', 'La girafe est l\'animal le plus grand en hauteur. Son long cou lui permet d\'atteindre les feuilles en hauteur.', 'images/animaux/girafe.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(4, 'Gorille', 'mammifere', 'Gorilla beringei', 'Le gorille est le plus grand des primates. C\'est un animal intelligent et social qui vit en groupe.', 'images/animaux/gorille.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(5, 'Dauphin', 'mammifere', 'Tursiops truncatus', 'Le dauphin est un mammifère marin très intelligent. Il est connu pour sa sociabilité et sa capacité à communiquer.', 'images/animaux/dauphin.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(6, 'Tigre', 'mammifere', 'Panthera tigris', 'Le plus grand des félins sauvages. Le tigre est un chasseur solitaire reconnaissable à ses rayures uniques.', 'images/animaux/tigre.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(7, 'Panda Géant', 'mammifere', 'Ailuropoda melanoleuca', 'Le panda géant est célèbre pour son pelage noir et blanc. Il se nourrit presque exclusivement de bambou.', 'images/animaux/panda.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(8, 'Kangourou', 'mammifere', 'Macropus rufus', 'Le kangourou se déplace en sautant et utilise sa queue pour l\'équilibre. La femelle porte son petit dans sa poche.', 'images/animaux/kangourou.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(9, 'Koala', 'mammifere', 'Phascolarctos cinereus', 'Le koala vit dans les arbres et se nourrit presque exclusivement de feuilles d\'eucalyptus. Il dort jusqu\'à 20 heures par jour !', 'images/animaux/koala.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(10, 'Aigle Royal', 'oiseau', 'Aquila chrysaetos', 'L\'aigle royal est un rapace majestueux avec une excellente vue. Il peut repérer ses proies à plus d\'un kilomètre.', 'images/animaux/aigle.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(11, 'Colibri', 'oiseau', 'Trochilidae', 'Le plus petit oiseau du monde ! Le colibri peut battre des ailes jusqu\'à 80 fois par seconde.', 'images/animaux/colibri.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(12, 'Perroquet Ara', 'oiseau', 'Ara macao', 'Le perroquet ara est connu pour ses plumes colorées et sa capacité à imiter la voix humaine.', 'images/animaux/perroquet.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(13, 'Pingouin Empereur', 'oiseau', 'Aptenodytes forsteri', 'Le plus grand des pingouins, capable de plonger jusqu\'à 500 mètres de profondeur pour chercher sa nourriture.', 'images/animaux/pingouin.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
(14, 'Paon', 'oiseau', 'Pavo cristatus', 'Le paon mâle est célèbre pour sa queue spectaculaire qu\'il déploie en éventail pour séduire les femelles.', 'images/animaux/paon.jpg', '2025-04-26 12:01:06', '2025-04-26 12:01:06'),
